function toc_temp(e) {
 var hc = 0;
 var l = 1;

 return `<div id='toc-post' class="d-block px-3 py-2 mb-3 mt-2 jt-bg-light rounded">
 <input id="toc-toggle" class="d-none" autocomplete="off" type="checkbox"/>
 <label for="toc-toggle" id="toc-toggler" class="toc-toggler d-flex align-items-center">
 <span class="fw-light pe-3">এই পোষ্টে থাকছে:</span>
 <span class="dropdown-toggle ms-auto"></span>
 </label>
 ${e.map(function(data, i){
 var n = data.level;
 var text = '';
 if( i  ==  0 ){
 text += '<ul class="ps-3 pt-3 d-none d-block-check fs-7">';
 }else if(hc < n){
 text += '<ul class="ps-3 pt-2 fs-8"><li>';
 l++;
 }else if(hc > n && l > 1){
 for(var m = 0; m < hc - n; m++){
 text += '</li></ul>';
 }
 l--;
 }
 hc = n;
 text += i > 0 ? '</li>' : '';
 text += '<li class="mb-2">';
 text += '<a class="text-reset text-decoration-none hover-text-primary" href="#'+data.id+'">'+data.title+'</a>';
 if( i  ==  e.length - 1 ){
 for(var j = 1; j < hc; j++){
 text += '</li></ul>';
 }
 }
 return text;
 }).join('').replace(/<li>\s*<\/li>/ig, '')}
 </div>`;
}

